// Extraire et structurer les donnÃ©es du lead
let data = {};

if (items && items.length > 0 && items[0].json && items[0].json.fields) {
  data = items[0].json.fields;
} else {
  return [{json: {error: 'No data received from trigger'}}];
}

// PrÃ©parer les donnÃ©es pour le scraping
const result = {
  ...data,
  recordId: items[0].json.id,
  // Nettoyer les donnÃ©es pour la recherche
  searchName: data.name ? data.name.replace(/[^a-zA-Z0-9 ]/g, '') : '',
  searchDomain: data.domain || '',
  searchCountry: data.country || 'France'
};

return [{ json: result }];

const companyData = items[0].json;
const financialData = items[1].json;
let scraperData = null;

// Tenter de parser les donnÃ©es financiÃ¨res
if (financialData && financialData.body) {
  try {
    scraperData = JSON.parse(financialData.body);
  } catch (e) {
    console.log('Error parsing financial data:', e);
  }
}

// Analyser les donnÃ©es financiÃ¨res si disponibles
let financialScore = 0;
let caEvolution = 'unknown';
let recentCA = null;
let urgency = 'medium';

if (scraperData && scraperData.entreprise) {
  // Extraire le CA rÃ©cent
  const finances = scraperData.entreprise.finances || [];
  const lastFinance = finances[0] || {};

  recentCA = lastFinance.chiffre_affaires;

  // Analyser l'Ã©volution sur 2-3 ans
  if (finances.length >= 2) {
    const currentCA = finances[0].chiffre_affaires || 0;
    const previousCA = finances[1].chiffre_affaires || 0;

    if (currentCA > previousCA * 1.2) {
      caEvolution = 'strong_growth';
      financialScore += 20;
      urgency = 'high';
    } else if (currentCA > previousCA * 1.1) {
      caEvolution = 'moderate_growth';
      financialScore += 10;
      urgency = 'medium';
    } else if (currentCA < previousCA * 0.9) {
      caEvolution = 'decline';
      financialScore += 15; // Les entreprises en difficultÃ© ont besoin d'aide
      urgency = 'high';
    } else {
      caEvolution = 'stable';
      financialScore += 5;
    }
  }

  // Ã‰valuer la taille (favoriser TPE/PME)
  if (recentCA) {
    if (recentCA < 2000000) { // < 2Mâ‚¬
      financialScore += 20; // TPE
    } else if (recentCA < 10000000) { // < 10Mâ‚¬
      financialScore += 15; // PME
    } else if (recentCA < 50000000) { // < 50Mâ‚¬
      financialScore += 5; // ETI
    }
  }
}

// Combiner avec les donnÃ©es existantes
const result = {
  ...companyData,
  financialData: {
    recentCA: recentCA,
    caEvolution: caEvolution,
    financialScore: financialScore,
    urgency: urgency
  },
  combinedUrgency: urgency,
  financialDataAvailable: !!scraperData
};

return [{ json: result }];

const data = items[0].json;
let score = 0;

// 1. Company size scoring (inversÃ© pour favoriser TPE/PME)
if (data.size_category) {
  switch(data.size_category) {
    case 'TPE (1-10)': score += 30; break;   // Prioritaire
    case 'PME (11-50)': score += 25; break;  // DeuxiÃ¨me prioritÃ©
    case 'ETI (51-250)': score += 15; break; // Moins prioritaire
    case 'GE (250+)': score += 5; break;     // Moins intÃ©ressant
  }
}

// 2. Industry scoring
const targetIndustries = ['Digital', 'E-commerce', 'Services', 'Retail', 'B2B Services'];
if (data.industry && targetIndustries.includes(data.industry)) {
  score += 20;
}

// 3. Geographic scoring
if (data.country === 'France') {
  score += 10;
}

// 4. Financial data scoring
if (data.financialData) {
  score += data.financialData.financialScore || 0;

  // Bonus pour croissance ou dÃ©clin (urgence)
  if (data.financialData.caEvolution === 'strong_growth' || 
      data.financialData.caEvolution === 'decline') {
    score += 10;
  }
}

// 5. DÃ©terminer la prioritÃ© finale
let finalPriority = 'low';
let urgencyLevel = data.financialData?.urgency || 'medium';

if (score > 70) {
  finalPriority = 'high';
} else if (score > 45) {
  finalPriority = 'medium';
}

// Ajuster selon l'urgence financiÃ¨re
if (urgencyLevel === 'high' && finalPriority !== 'low') {
  finalPriority = 'high';
}

// MÃ©tadonnÃ©es de scoring
const scoreBreakdown = {
  companySize: data.size_category ? 30 : 0,
  industry: (data.industry && targetIndustries.includes(data.industry)) ? 20 : 0,
  geography: data.country === 'France' ? 10 : 0,
  financial: data.financialData?.financialScore || 0,
  urgencyBonus: (data.financialData?.caEvolution === 'strong_growth' || 
                data.financialData?.caEvolution === 'decline') ? 10 : 0
};

const result = {
  ...data,
  leadScore: score,
  priority: finalPriority,
  urgency: urgencyLevel,
  scoreBreakdown: scoreBreakdown,
  financialInsights: {
    ca: data.financialData?.recentCA || 'unknown',
    evolution: data.financialData?.caEvolution || 'unknown',
    recommendedApproach: urgencyLevel === 'high' ? 'Immediate contact' : 
                        urgencyLevel === 'medium' ? 'Schedule within 48h' : 
                        'Add to nurturing campaign'
  }
};

return [{ json: result }];

const lead = items[0].json;
const salesRepsList = items[1].json;

if (!salesRepsList || salesRepsList.length === 0) {
  return [{json: {error: 'No sales reps available'}}];
}

// Assignation basÃ©e sur l'urgence et la spÃ©cialisation
let assignedRep;

// Exemple : assigner les leads urgents aux commerciaux seniors
if (lead.urgency === 'high') {
  // Chercher un commercial senior ou expÃ©rimentÃ©
  assignedRep = salesRepsList.find(rep => rep.fields?.experience_level === 'senior') || salesRepsList[0];
} else {
  // Round-robin pour les leads normaux
  assignedRep = salesRepsList[0];
}

// DÃ©finir les actions selon le profil et l'urgence
let nextActions = [];

if (lead.urgency === 'high') {
  nextActions = [
    { type: 'Call', priority: 'urgent', deadline: '2h', template: 'urgent_outreach' },
    { type: 'Email', template: 'urgent_opportunity' }
  ];
} else if (lead.priority === 'high') {
  nextActions = [
    { type: 'Email', template: 'tpe_pme_intro', timing: 'immediate' },
    { type: 'Call', priority: 'high', deadline: '24h' }
  ];
} else if (lead.priority === 'medium') {
  nextActions = [
    { type: 'Email', template: 'standard_intro' },
    { type: 'Call', priority: 'medium', deadline: '48h' }
  ];
} else {
  nextActions = [
    { type: 'Email', template: 'nurturing_sequence' }
  ];
}

// Personnaliser le message selon les insights financiers
let personalizedMessage = '';
if (lead.financialData?.caEvolution === 'strong_growth') {
  personalizedMessage = 'Company showing strong growth - expansion opportunity';
} else if (lead.financialData?.caEvolution === 'decline') {
  personalizedMessage = 'Company facing challenges - solution urgently needed';
} else {
  personalizedMessage = 'Stable company - focus on value proposition';
}

return [{
  json: {
    leadId: lead.uuid || lead.recordId,
    leadRecordId: lead.recordId,
    assignedTo: assignedRep.uuid || assignedRep.fields?.uuid,
    assignedEmail: assignedRep.email || assignedRep.fields?.email,
    leadScore: lead.leadScore,
    priority: lead.priority,
    urgency: lead.urgency,
    nextActions: nextActions,
    companyName: lead.name || 'Unknown Company',
    financialInsights: lead.financialInsights,
    personalizedMessage: personalizedMessage,
    assignmentReason: `${lead.urgency.toUpperCase()} urgency - TPE/PME focus`,
    approachRecommendation: lead.financialInsights?.recommendedApproach
  }
}];
