// Extraire et structurer les donn√©es du lead
let data = {};

if (items && items.length > 0 && items[0].json && items[0].json.fields) {
  data = items[0].json.fields;
} else {
  return [{json: {error: 'No data received from trigger'}}];
}

// Pr√©parer les donn√©es pour le scraping
const result = {
  ...data,
  recordId: items[0].json.id,
  // Nettoyer les donn√©es pour la recherche
  searchName: data.name ? data.name.replace(/[^a-zA-Z0-9 ]/g, '') : '',
  searchDomain: data.domain || '',
  searchCountry: data.country || 'France',
  // Pr√©parer pour logs
  scrapingStartTime: Date.now()
};

return [{ json: result }];

const companyData = items[0].json;
const scraperResponse = items[1].json;
const scrapingLog = items[2].json;

let financialEntries = [];
let latestFinancial = null;
let financialScore = 0;
let revenueTrend = 'Unknown';
let scrapingSuccess = false;

// Parser les donn√©es si disponibles
if (scraperResponse && scraperResponse.body && scraperResponse.body.entreprise) {
  const entreprise = scraperResponse.body.entreprise;
  const finances = entreprise.finances || [];

  if (finances.length > 0) {
    scrapingSuccess = true;

    // Cr√©er une entr√©e pour chaque ann√©e disponible
    finances.forEach((finance, index) => {
      if (finance && finance.date_cloture_exercice) {
        const year = new Date(finance.date_cloture_exercice).getFullYear();
        const revenue = finance.chiffre_affaires || 0;

        financialEntries.push({
          uuid: `${companyData.recordId}-${year}`,
          company_id: companyData.recordId,
          source: 'Pappers',
          year: year,
          revenue: revenue,
          employees: finance.resultat_net || 0,
          raw_data: JSON.stringify(finance),
          confidence_score: 85,
          last_updated: new Date().toISOString(),
          created_at: new Date().toISOString()
        });
      }
    });

    // Analyser la tendance
    if (finances.length >= 2) {
      const currentCA = finances[0]?.chiffre_affaires || 0;
      const previousCA = finances[1]?.chiffre_affaires || 0;
      const growthRate = previousCA > 0 ? ((currentCA - previousCA) / previousCA) * 100 : 0;

      if (growthRate > 20) {
        revenueTrend = 'Strong Growth';
        financialScore += 25;
      } else if (growthRate > 10) {
        revenueTrend = 'Moderate Growth';
        financialScore += 15;
      } else if (growthRate < -10) {
        revenueTrend = 'Decline';
        financialScore += 20; // Les entreprises en difficult√© aussi
      } else {
        revenueTrend = 'Stable';
        financialScore += 10;
      }

      // Pr√©parer les donn√©es les plus r√©centes
      latestFinancial = {
        revenue: currentCA,
        growth_rate: growthRate,
        year: new Date(finances[0].date_cloture_exercice).getFullYear()
      };
    } else if (finances.length === 1) {
      // Une seule ann√©e disponible
      const currentCA = finances[0]?.chiffre_affaires || 0;
      financialScore += 10; // Score de base pour info disponible
      latestFinancial = {
        revenue: currentCA,
        growth_rate: 0,
        year: new Date(finances[0].date_cloture_exercice).getFullYear()
      };
    }

    // Score bas√© sur la taille (favoriser TPE/PME)
    if (latestFinancial && latestFinancial.revenue) {
      if (latestFinancial.revenue < 2000000) {
        financialScore += 30; // TPE
      } else if (latestFinancial.revenue < 10000000) {
        financialScore += 25; // PME
      } else if (latestFinancial.revenue < 50000000) {
        financialScore += 15; // ETI
      } else {
        financialScore += 5; // GE
      }
    }
  }
}

return [{
  json: {
    ...companyData,
    financialEntries: financialEntries,
    latestFinancial: latestFinancial,
    financialScore: financialScore,
    revenueTrend: revenueTrend,
    scrapingSuccess: scrapingSuccess
  }
}];

// Cette fonction pr√©pare les donn√©es pour stocker l'historique financier
const data = items[0].json;

// Si on a des donn√©es financi√®res √† stocker
if (data.financialEntries && data.financialEntries.length > 0) {
  // On renvoie chaque entr√©e comme une op√©ration s√©par√©e
  const results = data.financialEntries.map(entry => ({
    json: {
      ...data,
      financialEntry: entry
    }
  }));

  return results;
} else {
  // Si pas de donn√©es, on continue quand m√™me
  return [{ json: data }];
}

const data = items[0].json;
let totalScore = 0;

// Base scoring (modifi√© pour TPE/PME)
if (data.size_category) {
  switch(data.size_category) {
    case 'TPE (1-10)': totalScore += 30; break;
    case 'PME (11-50)': totalScore += 25; break;
    case 'ETI (51-250)': totalScore += 15; break;
    case 'GE (250+)': totalScore += 5; break;
  }
}

// Ajout du score financier
totalScore += data.financialScore || 0;

// D√©terminer la priorit√© bas√©e sur le score et la tendance
let finalPriority = 'low';
let urgency = 'medium';

if (totalScore > 80) {
  finalPriority = 'high';
} else if (totalScore > 60) {
  finalPriority = 'medium';
}

// Ajuster bas√© sur la tendance financi√®re
if (data.revenueTrend === 'Strong Growth' || data.revenueTrend === 'Decline') {
  urgency = 'high';
  if (finalPriority !== 'low') {
    finalPriority = 'high';
  }
}

return [{
  json: {
    ...data,
    leadScore: totalScore,
    priority: finalPriority,
    urgency: urgency,
    actionableInsights: {
      message: data.revenueTrend === 'Strong Growth' 
        ? 'Entreprise en forte croissance - opportunit√© d\'expansion'
        : data.revenueTrend === 'Decline'
        ? 'Entreprise en difficult√© - solution urgente n√©cessaire'
        : 'Entreprise stable - focus sur la valeur ajout√©e',
      recommendedTimeline: urgency === 'high' ? '2 heures' : finalPriority === 'high' ? '24 heures' : '48 heures'
    }
  }
}];

const lead = items[0].json;
const salesReps = items[1].json;

if (!salesReps || salesReps.length === 0) {
  return [{json: {error: 'No sales reps available'}}];
}

// Assignment intelligent bas√© sur urgency et expertise
let assignedRep = salesReps[0]; // Default

if (lead.urgency === 'high') {
  // Chercher un commercial senior
  const seniorRep = salesReps.find(rep => 
    rep.fields?.experience_level === 'senior' || 
    rep.fields?.title?.includes('Senior')
  );
  if (seniorRep) assignedRep = seniorRep;
}

// Actions contextuelles
let nextActions = [];
if (lead.urgency === 'high') {
  nextActions = [
    { 
      type: 'Call', 
      priority: 'urgent', 
      deadline: '2h',
      note: `${lead.revenueTrend} - ${lead.actionableInsights.message}`
    },
    { 
      type: 'Email', 
      template: 'urgent_outreach',
      context: lead.actionableInsights.message
    }
  ];
} else {
  nextActions = [
    { 
      type: 'Email', 
      template: 'tpe_pme_intro',
      context: lead.actionableInsights.message
    },
    { 
      type: 'Call', 
      priority: lead.priority, 
      deadline: lead.actionableInsights.recommendedTimeline
    }
  ];
}

return [{
  json: {
    ...lead,
    assignedTo: assignedRep.uuid || assignedRep.fields?.uuid,
    assignedEmail: assignedRep.email || assignedRep.fields?.email,
    nextActions: nextActions,
    financialContext: {
      revenue: lead.latestFinancial?.revenue || 'unknown',
      trend: lead.revenueTrend,
      confidence: 'High' // Bas√© sur Pappers
    }
  }
}];
